mapper.select:
select

from wx_dict_data
where 1=1

  and id = #{id,jdbcType=BIGINT}


  and content = #{content,jdbcType=VARCHAR}

mapper.insert:
insert into wx_dict_data (id, content, pron, 
  uk_pron, audio, uk_audio, 
  definition, example, create_time
  )
values (#{id,jdbcType=BIGINT}, #{content,jdbcType=VARCHAR}, #{pron,jdbcType=VARCHAR}, 
  #{ukPron,jdbcType=VARCHAR}, #{audio,jdbcType=VARCHAR}, #{ukAudio,jdbcType=VARCHAR}, 
  #{definition,jdbcType=VARCHAR}, #{example,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )
